                "parameters":{
                  "gte":1
                }
              },
              {
                "type":"range",
                "field":"children.stock",
                "parameters":{
                  "gte":1
                }
              }
            ]
          }
        ],
        "associations":{
            "children": {},
            "cover": {}
        },
        "includes": {
                "product":[
            "id",
            "name"
                ]
        },
        "total-count-mode":1
      }


      const getProducts = async(credentials) => {
      const login = {
          "client_id": "administration",
          "grant_type": "password",
          "scopes": "write",
          "username": credentials.email,
          "password": credentials.password
      }

