import React, { useState } from "react";
import { useNavigate } from  'react-router-dom';
import { useFormik } from 'formik';

function Login() {
    const navigate = useNavigate();

 let [products, setProducts] = useState();
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      getProducts(values)
      navigate('/');
    },
  });
    const body = {
        "page":1,
        "filter":[
          {
            "type":"equals",
            "field":"product.parentId",
            "value": null
          },
          {
            "type":"equals",
            "field":"product.active",
            "value":false
          },
          {
            "type":"multi",
            "operator":"or",
            "queries":[
              {
                "type":"range",
                "field":"stock",
                "parameters":{
                  "gte":1
                }
              },
              {
                "type":"range",
                "field":"children.stock",
                "parameters":{
                  "gte":1
                }
              }
            ]
          }
        ],
        "associations":{
            "children": {},
            "cover": {}
        },
        "includes": {
                "product":[
            "id",
            "name"
                ]
        },
        "total-count-mode":1
      }


      const getProducts = async(credentials) => {
      const login = {
          "client_id": "administration",
          "grant_type": "password",
          "scopes": "write",
          "username": credentials.email,
          "password": credentials.password
      }

        const fetchToken = await fetch('https://www.freshcotton.com/api/oauth/token', { 'method': 'POST', 'headers' : {"Content-Type" : "application/json" },  body : JSON.stringify(login)})
        const convertToken  = await fetchToken.json();
        // const fetchProducts = await fetch('https://www.freshcotton.com/api/search/product', { 'method': 'POST', 'headers' : { "Content-Type" : "application/json" , 'Authorization' : `Bearer ${convertToken.access_token}`} , body: JSON.stringify(body)})
        // const convertProducts = await fetchProducts.json();
        // setProducts(() => convertProducts) 
      let auth = window.sessionStorage.setItem('RANDOM', convertToken.access_token)

      }


  return (
    <>
<section className="h-100 gradient-form py-5 my-5">
  <div className="container py-5 h-100">
    <div className="row d-flex justify-content-center align-items-center h-100">
      <div className="col-xl-10">
        <div className="card rounded-3 text-black">
          <div className="row g-0">
          <div className="col-lg-6 d-flex align-items-center gradient-custom-2" style={{backgroundImage: `url("https://www.freshcotton.com/media/04/fc/e9/1667904445/Tekengebied%202_(3).png")` }}>
            </div>
            <div className="col-lg-6">
              <div className="card-body p-md-5 mx-md-4">
                <div className="text-center">
                  <img src="https://cdn.webshopapp.com/shops/246937/themes/174045/assets/logo.svg?20221031105537" style={{width: '185px'}} alt="logo" className="py-4"/>
                  <h4 className="mt-1 mb-5 pb-1">Freshkèts Tools</h4>
                </div>
                <form onSubmit={formik.handleSubmit}>
                  <p>Please login to your account</p>
                  <div className="form-outline mb-4">
                    <input id="email" name="email" type="text" onChange={formik.handleChange} value={formik.values.email} className="form-control" placeholder="Phone number or email address" />
                    <label className="form-label" htmlFor="form2Example11">Username</label>
                  </div>
                  <div className="form-outline mb-4">
                    <input id="password" name="password" type="password" onChange={formik.handleChange} value={formik.values.password} className="form-control" />
                    <label className="form-label" htmlFor="form2Example22">Password</label>
                  </div>
                  <button class="btn btn-primary btn-lg" type="submit">Login</button>
                </form>
              </div>
            </div>

          </div>
        </div>
      </div>
    </div>
  </div>
</section>
    </>
  )
}

export default Login
