import React, { useState } from "react";
import { useFormik } from 'formik';

const App = () => {
  let [products, setProducts] = useState();
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      getProducts(values)
    },
  });

const body = {
  "page":1,
  "filter":[
    {
      "type":"equals",
      "field":"product.parentId",
      "value": null
    },
    {
      "type":"equals",
      "field":"product.active",
      "value":false
    },
    {
      "type":"multi",
      "operator":"or",
      "queries":[
        {
          "type":"range",
          "field":"stock",
          "parameters":{
            "gte":1
          }
        },
        {
          "type":"range",
          "field":"children.stock",
          "parameters":{
            "gte":1
          }
        }
      ]
    }
  ],
  "associations":{
      "children": {},
      "cover": {}
  },
  "includes": {
          "product":[
      "id",
      "name"
          ]
  },
  "total-count-mode":1
}


const getProducts = async(credentials) => {
const login = {
    "client_id": "administration",
    "grant_type": "password",
    "scopes": "write",
    "username": credentials.email,
    "password": credentials.password
}

  const fetchToken = await fetch('https://www.freshcotton.com/api/oauth/token', { 'method': 'POST', 'headers' : {"Content-Type" : "application/json" },  body : JSON.stringify(login)})
  const convertToken  = await fetchToken.json();
  const fetchProducts = await fetch('https://www.freshcotton.com/api/search/product', { 'method': 'POST', 'headers' : { "Content-Type" : "application/json" , 'Authorization' : `Bearer ${convertToken.access_token}`} , body: JSON.stringify(body)})
  const convertProducts = await fetchProducts.json();
  setProducts(() => convertProducts) 
  window.localStorage.setItem('token', convertToken.access_token)

}



  return (
    <>
    <form onSubmit={formik.handleSubmit}>

      <label htmlFor="email">Username</label>
      <input
        id="email"
        name="email"
        type="text"
        onChange={formik.handleChange}
        value={formik.values.email}
      />
       <label htmlFor="password">Password</label>
      <input
        id="password"
        name="password"
        type="password"
        onChange={formik.handleChange}
        value={formik.values.password}
      />
      <button type="submit">Submit</button>
      </form>

      <h1 className="m-3">Product availability check</h1>

      <table className="table">
      <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Name</th>
      <th scope="col">URL</th>
      <th scope="col">IMAGE</th> 
    </tr>
  </thead>
  <tbody>
      { products  && products.data.map(product => {
        return( 
        <tr>
          <td>{product.id}</td>
          <td>{product.attributes.name}</td>
          <td><a href={`https://www.freshcotton.com/admin#/sw/product/detail/${product.id}/base`}> View Product in Shopware</a></td>
          <td><img src=""/></td>
          {/* <img src="https://www.freshcotton.com/media/bd/41/59/1659098369/dwhbd-5.jpg" itemprop="thumbnail" alt="Image description" class="img-fluid"> */}
          </tr>)
      } )}
        </tbody>

      </table>
      </>
  );
};

export default App;
