import React from 'react'

function Pac() {

    const getProducts = async(credentials) => {
        const login = {
            "client_id": "administration",
            "grant_type": "password",
            "scopes": "write",
            "username": credentials.email,
            "password": credentials.password
        }

          const fetchToken = await fetch('https://www.freshcotton.com/api/oauth/token', { 'method': 'POST', 'headers' : {"Content-Type" : "application/json" },  body : JSON.stringify(login)})
          const convertToken  = await fetchToken.json();
          const fetchProducts = await fetch('https://www.freshcotton.com/api/search/product', { 'method': 'POST', 'headers' : { "Content-Type" : "application/json" , 'Authorization' : `Bearer ${convertToken.access_token}`} , body: JSON.stringify(body)})
          const convertProducts = await fetchProducts.json();
          setProducts(() => convertProducts) 
          window.localStorage.setItem('token', convertToken.access_token)

        }
  return (
    <>
    <div>Pac</div>



    <div className="my-5 mx-3">
      <h1>Product availability check</h1>
      <button className="btn btn-primary my-2" > Export to cvs </button>
      </div>

      <table className="table  mx-3">
      <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Name</th>
      <th scope="col">URL</th>
      <th scope="col">Image</th> 
    </tr>
  </thead>
  <tbody>
      { products  && products.data.map(product => {
        return( 
        <tr>
          <td>{product.id}</td>
          <td>{product.attributes.name}</td>
          <td><a href={`https://www.freshcotton.com/admin#/sw/product/detail/${product.id}/base`}> View Product in Shopware</a></td>
          <td><img className="img-fluid img-thumbnail" src={`https://www.freshcotton.com/api/product/${product.attributes.coverId}/cover`} /></td>
          </tr>)
      } )}
        </tbody>

      </table>

    </>
  )
}

export default Pac
