import { Outlet, Navigate } from 'react-router-dom'
// import {auth} from '../pages/Login';

// import Login from '../pages/Login'



const PrivateRoute = () => {

     const getProducts = async(credentials) => {
        const login = {
            "client_id": "administration",
            "grant_type": "password",
            "scopes": "write",
            "username": credentials.email,
            "password": credentials.password
        }

          const fetchToken = await fetch('https://www.freshcotton.com/api/oauth/token', { 'method': 'POST', 'headers' : {"Content-Type" : "application/json" },  body : JSON.stringify(login)})
          const convertToken  = await fetchToken.json();

         window.localStorage.setItem('token', convertToken.access_token)
         let token = window.localStorage.getItem('token', convertToken.access_token)

        }
        let auth = {'token': token}
    // console.log(window.localStorage.getItem('token', convertToken.access_token))

    // hier moet gekeken worden naar of de token klopt
  return (

    auth.token ? <Outlet/> : <Navigate to="/login"/>
  )
}

export default PrivateRoute
